// SPDX-License-Identifier: MIT
pragma solidity <0.7.0;

contract TransferContract {

    address owner; 
    address senderAG; 
    address receiverAG;
    address receiver;
    address sender;

      // 登記雙方AG的意見
    mapping(address=>bool) agreements;

      // 本次交易總金額
    uint256 totalAmount;
      // 交易目前餘額
    uint256 currentAmount;

      // 後續扣款的次數
    uint256 step;

      //  交易雜湊的陣列
    uint256[] transactionSignature;


  // 設定合約初始化
    constructor(address _senderAG, address _receiverAG, address _sender, address _receiver) public {
        owner = msg.sender;
        senderAG = _senderAG;
        receiverAG = _receiverAG;
        sender = _sender;
        receiver = _receiver;

        agreements[senderAG] = false;
        agreements[receiverAG] = false;

        step=0;
        totalAmount=0;
        currentAmount=0;
    }


      // 設定函數條件
    modifier onlyCA{
        require(msg.sender == owner, "CA Authentication failed");
        _;
    }

    modifier onlyAG{
        require(msg.sender == senderAG || msg.sender == receiverAG , "AG Authentication failed");
        _;
    }

    modifier agreementAG{
        require(agreements[senderAG]==false, "Sender AG have no agreement");
        require(agreements[receiverAG]==false, "Receiver AG have no agreement");
        _;
    }

    modifier paymentRequire(address _from, address _to, uint256 _balance){
        require(_from == sender && _to ==receiver && _balance<=currentAmount, "paymentRequire is not satisfied");
        _;
    }

// 可用函數

/*
      function setAG(address _AG1, address _AG2) public onlyCA {
          // 設定AG
          senderAG = _AG1;
          receiverAG = _AG2;

          agreements[senderAG] = false;
          agreements[receiverAG] = false;
      }
 */   
        function getAG() public view returns(address, address){ 
          // 取得AG 
              return (senderAG, receiverAG);
        }


        function setAgreement(bool status) public onlyAG{
            // 供AG設定其是否同意交易
            agreements[msg.sender] = status;
        }

        function createTransaction(address _from, address _to, uint256 balance, uint256 signature) public onlyCA returns(bool){
              // 開啟交易只需要CA同意即可
            require(msg.sender == senderAG, "only senderAG is supported");
            sender = _from;
            receiver = _to;
            totalAmount = balance;

          // 這次交易最一開始的簽章
            transactionSignature.push(signature);

            return true;
        }


        function Payment(address _from, address _to, uint256 _balance, uint256 signature) public onlyAG agreementAG paymentRequire(_from,_to,_balance) returns(bool){
            require(address(this).balance!=(totalAmount+currentAmount), "Sender hasn't pay any balance to contract yet");

            totalAmount -= _balance; // 餘額
            currentAmount += _balance; // 累積的費用
            transactionSignature.push(signature);

           // 把 AG的agreement 改回 false
            agreements[senderAG]=false;
            agreements[receiverAG] =false;

            return true;
        }


        function closeTransaction(address _from, address _to) public onlyCA agreementAG returns(bool){
          // 關閉合約需要 CA，雙方AG同意才可    
            address payable payfrom = payable(_from);
            address payable payto = payable(_to);

          // 歸還餘額給sender
            if (totalAmount>0)
                payfrom.transfer(totalAmount);

          // 把目前累積的費用轉給 receiver
            payto.transfer(currentAmount);
            selfdestruct(payable(owner));
            return true;//
        }





}
